package net.nekomura.ytsubnumshower;

import java.io.File;
import java.io.IOException;

import javax.swing.ImageIcon;

public class Image {
	
	static String iconImage = "";
	
	static String bgImage = "";
	static String stripeImage = "";
	
	static String noAdvatarImage = "";
	
	static String crossButtonImage = "";
	static String settingButtonImage = "";
	
	public static ImageIcon getIconImage() throws IOException {
		File bg = new File("res\\icon\\icon.png");
		if(bg.exists()) {
			return new ImageIcon("res\\icon\\icon.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(iconImage));
		}
	}
	
	public static ImageIcon getBgImage() throws IOException {
		File bg = new File("res\\UI\\bg.png");
		if(bg.exists()) {
			return new ImageIcon("res\\UI\\bg.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(bgImage));
		}
	}
	
	public static ImageIcon getStripeImage() throws IOException {
		File bg = new File("res\\UI\\stripe.png");
		if(bg.exists()) {
			return new ImageIcon("res\\UI\\stripe.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(stripeImage));
		}
	}
	
	public static ImageIcon getNoAdvatarImage() throws IOException {
		File bg = new File("res\\misc\\NoAdvatar.png");
		if(bg.exists()) {
			return new ImageIcon("res\\misc\\NoAdvatar.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(noAdvatarImage));
		}
	}
	
	public static ImageIcon getCrossButtonImage() throws IOException {
		File bg = new File("res\\button\\cross.png");
		if(bg.exists()) {
			return new ImageIcon("res\\button\\cross.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(crossButtonImage));
		}
	}
	
	public static ImageIcon getSettingButtonImage() throws IOException {
		File bg = new File("res\\button\\setting.png");
		if(bg.exists()) {
			return new ImageIcon("res\\button\\setting.png");
		}else {
			return new ImageIcon(ImageUtils.getFromBASE64(settingButtonImage));
		}
	}
}
